<?php

namespace FootstatBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * MatchesRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class MatchesRepository extends EntityRepository
{
    public function byChampionnat($championnat){
        $db = $this->createQueryBuilder('u')
                ->select('u')
                ->where('u.championnat = :championnat')
                ->orderBy('u.id')
                ->setParameter('championnat', $championnat)
                ->orderBy('u.date', 'ASC')
                ->setMaxResults(10);
        return $db->getQuery()->getResult();                
    }
    
    public function byDate($date){
        $db = $this->createQueryBuilder('u')
                ->select('u')
                ->where('u.date = :date')
                
                ->setParameter('date', $date." %");
        return $db->getQuery()->getResult();
                
    }
   
    public function byNextmatchs($equipe){
        $db = $this->createQueryBuilder('u')
                ->select('u')
                ->andWhere('u.equipeDom = :equipe')
                ->orWhere('u.equipeExt = :equipe')
                ->andWhere('u.type = 1')
                ->orderBy('u.id')
                ->setParameter('equipe', $equipe);
        return $db->getQuery()->getResult();
                
    }
}
